import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Button,
  Container,
  Row,
  Col,
  ListGroup,
  ListGroupItem
} from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";

import piloteActions from "../../redux/actions/piloteActions";
import { getPilote } from "../../redux/reducers/piloteReducer";

export function Pilote() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { idPilote, anneeEnCours } = useParams();

  const pilote = useSelector(getPilote);

  const { nomPilote, ecuriePilote, photoPilote, points, nationalite } = pilote;

  useEffect(() => {
    dispatch(piloteActions.getPiloteByIdAndYear({ anneeEnCours, idPilote }));
  }, [dispatch]);

  const onClickRetour = () => {
    navigate("/pilotes");
    dispatch(piloteActions.clearPilote());
  };

  return (
    <div className="Pilotes">
      <h2 className="title-page">{nomPilote}</h2>
      <Container>
        <Row>
          <Col md={2}>
            <Button variant="outline-primary" onClick={onClickRetour}>
              Retour
            </Button>
          </Col>
        </Row>
        <Row className="mt-3">
          <Col md={4}>
            <ListGroup>
              <ListGroupItem>Écurie : {ecuriePilote}</ListGroupItem>
              <ListGroupItem>Nationalité : {nationalite}</ListGroupItem>
              <ListGroupItem>Année en cours : {anneeEnCours}</ListGroupItem>
              <ListGroupItem>Nombre de points : {points}</ListGroupItem>
            </ListGroup>
          </Col>
          <Col md={{ span: 2, offset: 3 }}>
            {pilote.photoPilote ? (
              <img
                className="imgPilote"
                alt="image"
                src={`/images/pilotes/${photoPilote.replace(
                  "C:\\fakepath\\",
                  ""
                )}`}
              />
            ) : null}
          </Col>
        </Row>
      </Container>
    </div>
  );
}
