import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row, Col, Form, Button, Container } from "react-bootstrap";
import userActions from "../../redux/actions/userActions";
import { getUsers } from "../../redux/reducers/userReducer";

export function Dashboard() {
  const dispatch = useDispatch();
  const users = useSelector(getUsers);

  useEffect(() => {
    dispatch(userActions.getUsers());
  }, [dispatch]);

  const handleClick = id => {
    console.log("id", id);
  };

  const handleSelect = (event, id) => {
    console.log("event.target", event.target);
    console.log("event.target", event.target.value);
  };

  return (
    <div className="Dashboard">
      <h1>Dashboard</h1>
      {users.map((user, index) => (
        <Container key={index} fluid="md">
          <Row className="mb-2">
            <Col sm={3}>{user.email}</Col>
            <Col sm={4}>
              <Form.Select
                defaultValue={user.droitsCreation}
                aria-label="Default select example"
                name="droitsCreation"
                onChange={event => handleSelect(event, user._id)}
              >
                <option value="true">true</option>
                <option value="false">false</option>
              </Form.Select>
            </Col>
            <Col sm={4}>
              <Form.Select
                defaultValue={user.droitsEdition}
                aria-label="Default select example"
                name="droitsEdition"
              >
                <option value="true">true</option>
                <option value="false">false</option>
              </Form.Select>
            </Col>
            <Col sm={1}>
              <Button onClick={() => handleClick(user._id)}>Appliquer</Button>
            </Col>
          </Row>
        </Container>
      ))}
    </div>
  );
}
