import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Button,
  Container,
  Row,
  Col,
  Form,
  ListGroup,
  ListGroupItem
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";

import piloteActions from "../../redux/actions/piloteActions";
import { getPilotes } from "../../redux/reducers/piloteReducer";
import configHelper from "../../helpers/configHelper";

export function Pilotes() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [anneeEnCours, setAnneeEnCours] = useState("2021");
  const [annees] = useState(configHelper.generateArrayOfYears());

  const pilotes = useSelector(getPilotes);

  const handleChangeAnnee = event => setAnneeEnCours(event.target.value);

  useEffect(() => {
    dispatch(piloteActions.getPilotes({ anneeEnCours }));
  }, [dispatch, anneeEnCours]);

  const onClickCreate = () => navigate("creer-pilote");

  return (
    <div className="Pilotes">
      <h2 className="title-page">Pilotes</h2>
      <Container>
        <Row>
          <Col md={2}>
            <Form.Select
              autoFocus
              name="anneeEnCours"
              value={anneeEnCours}
              onChange={handleChangeAnnee}
            >
              {annees.map((annee, index) => (
                <option key={index} value={annee.value}>
                  {annee.label}
                </option>
              ))}
            </Form.Select>
          </Col>
          <Col md={{ span: 2, offset: 8 }}>
            <Button variant="outline-primary" onClick={onClickCreate}>
              Cr√©er un pilote
            </Button>
          </Col>
        </Row>
        <Row className="mt-3">
          <Col>
            <ListGroup>
              {pilotes.map((pilote, index) => {
                return (
                  <ListGroupItem key={index}>
                    <p>{`${pilote.nomPilote} - ${pilote.ecuriePilote}`}</p>
                  </ListGroupItem>
                );
              })}
            </ListGroup>
          </Col>
        </Row>
      </Container>
    </div>
  );
}
