import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row, Col, Form, Button, Container } from "react-bootstrap";
import userActions from "../../redux/actions/userActions";
import { getUsers } from "../../redux/reducers/userReducer";

export function Dashboard() {
  const dispatch = useDispatch();
  const users = useSelector(getUsers);
  const [newUsers, setNewUsers] = useState([]);
  console.log("users", users);

  useEffect(() => {
    dispatch(userActions.getUsers()).then(res => setNewUsers(res));
  }, [dispatch]);
  console.log("newUsers", newUsers);

  const handleClick = id => {
    console.log("id", id);
    const user = newUsers.find(user => user._id === id);
    console.log("user", id);
  };

  const handleSelect = (event, currentUser) => {
    const index = newUsers.findIndex(user => user._id);
    newUsers[index] = {
      ...currentUser,
      [event.target.name]: event.target.value
    };
  };

  return (
    <div className="Dashboard">
      <h1>Dashboard</h1>
      {newUsers.map((user, index) => (
        <Container key={index} fluid="md">
          <Row className="mb-2">
            <Col sm={3}>{user.email}</Col>
            <Col sm={4}>
              <Form.Select
                defaultValue={user.droitsCreation}
                aria-label="Default select example"
                name="droitsCreation"
                onChange={event => handleSelect(event, user)}
              >
                <option value="true">true</option>
                <option value="false">false</option>
              </Form.Select>
            </Col>
            <Col sm={4}>
              <Form.Select
                defaultValue={user.droitsEdition}
                aria-label="Default select example"
                name="droitsEdition"
                onChange={event => handleSelect(event, user)}
              >
                <option value="true">true</option>
                <option value="false">false</option>
              </Form.Select>
            </Col>
            <Col sm={1}>
              <Button onClick={() => handleClick(user._id)}>Appliquer</Button>
            </Col>
          </Row>
        </Container>
      ))}
    </div>
  );
}
