import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Button,
  Container,
  Row,
  Col,
  Form,
  Carousel,
  CarouselItem
} from "react-bootstrap";
import { useNavigate } from "react-router-dom";

import piloteActions from "../../redux/actions/piloteActions";
import { getPilotes } from "../../redux/reducers/piloteReducer";
import { generateArrayOfYears } from "../../helpers/yearHelper";

export function Pilotes() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [anneeEnCours, setAnneeEnCours] = useState("2021");
  const [annees] = useState(generateArrayOfYears());

  const pilotes = useSelector(getPilotes);

  const handleChangeAnnee = event => setAnneeEnCours(event.target.value);

  useEffect(() => {
    dispatch(piloteActions.getPilotes({ anneeEnCours }));
  }, [dispatch, anneeEnCours]);

  const onClickCreate = () => navigate("creer-pilote");

  return (
    <div className="Pilotes">
      <h2 className="title-page">Pilotes</h2>
      <Container>
        <Row>
          <Col md={2}>
            <Form.Select
              autoFocus
              name="anneeEnCours"
              value={anneeEnCours}
              onChange={handleChangeAnnee}
            >
              {annees.map((annee, index) => (
                <option key={index} value={annee}>
                  {annee}
                </option>
              ))}
            </Form.Select>
          </Col>
          <Col md={{ span: 2, offset: 8 }}>
            <Button variant="outline-primary" onClick={onClickCreate}>
              Cr√©er un pilote
            </Button>
          </Col>
        </Row>
      </Container>
      <Carousel variant="dark">
        {/* {pilotes.map((pilote, index) => {
          return ( */}
        <Carousel.Item key={index}>
          <Carousel.Caption>
            <p>{`test`}</p>
          </Carousel.Caption>
        </Carousel.Item>
        {/* );
        })} */}
      </Carousel>
    </div>
  );
}
