import ecurieApi from "../../API/ecurieApi";
import { ecurieConstants } from "../../constants/ecurieConstants";
import { commonConstants } from "../../constants/commonConstants";

const createEcurie = ecurie => dispatch => {
  return ecurieApi
    .createEcurie(ecurie)
    .then(res => {
      dispatch({ type: ecurieConstants.CREATE_ECURIE, res });
      dispatch({
        type: commonConstants.NOTIFICATION_HANDLING,
        payload: res
      });
    })
    .catch(err => {
      dispatch({
        type: commonConstants.NOTIFICATION_HANDLING,
        payload: err.response
      });

      console.log("test de l'erreur", err.response);
    });
};
const getEcuries = anneeEnCours => dispatch => {
  return ecurieApi
    .getEcuries(anneeEnCours)
    .then(res => {
      dispatch({
        type: ecurieConstants.GET_ECURIES,
        payload: res.data.ecuries
      });
    })
    .catch(err => {
      dispatch({
        type: commonConstants.NOTIFICATION_HANDLING,
        payload: err.response
      });

      console.log("test de l'erreur", err.response);
    });
};

const ecurieActions = {
  createEcurie,
  getEcuries
};
export default ecurieActions;
