import { ecurieConstants } from "../../constants/ecurieConstants";

const initialState = {
  ecurie: {},
  ecuries: []
};

export function ecurieReducer(state = initialState, action) {
  switch (action.type) {
    case ecurieConstants.CREATE_ECURIE:
      return {
        ...state,
        ecurie: action.ecurie
      };
    case ecurieConstants.GET_ECURIES:
      return {
        ...state,
        ecuries: action.payload
      };
    default:
      return state;
  }
}

export const getEcurie = state => {
  return state.ecurie.ecurie;
};
export const getEcuries = state => {
  return state.ecurie.ecuries;
};

export default ecurieReducer;
