import { piloteConstants } from "../../constants/piloteConstants";

const initialState = {
  pilote: {},
  pilotes: []
};

export function piloteReducer(state = initialState, action) {
  switch (action.type) {
    case piloteConstants.CREATE_PILOTE:
      return {
        ...state,
        pilote: action.pilote
      };
    case piloteConstants.GET_PILOTES:
      return {
        ...state,
        pilotes: action.payload
      };
    case piloteConstants.GET_PILOTE:
      return {
        ...state,
        pilote: action.payload
      };
    default:
      return state;
  }
}

export const getPilote = state => {
  return state.pilote.pilote;
};
export const getPilotes = state => {
  return state.pilote.pilotes;
};

export default piloteReducer;
