import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  Button,
  Container,
  Row,
  Col,
  Form,
  ListGroup,
  ListGroupItem
} from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";

import piloteActions from "../../redux/actions/piloteActions";
import { getPilote } from "../../redux/reducers/piloteReducer";
import configHelper from "../../helpers/configHelper";

export function Pilote() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { idPilote, anneeEnCours } = useParams();

  const pilote = useSelector(getPilote);

  const { nomPilote, ecuriePilote, photoPilote } = pilote;

  useEffect(() => {
    dispatch(piloteActions.getPiloteByIdAndYear({ anneeEnCours, idPilote }));
  }, [dispatch]);

  const onClickRetour = () => navigate("/pilotes");

  return (
    <div className="Pilotes">
      <h2 className="title-page">Pilotes</h2>
      <Container>
        <Row>
          <Col md={2}>
            <Button variant="outline-primary" onClick={onClickRetour}>
              Retour
            </Button>
          </Col>
        </Row>
        <Row className="mt-3">
          <Col></Col>
        </Row>
      </Container>
    </div>
  );
}
