import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

import piloteActions from "../../redux/actions/piloteActions";
import { getPilotes } from "../../redux/reducers/piloteReducer";

export function Pilotes() {
  const dispatch = useDispatch();

  const pilotes = useSelector(getPilotes);

  useEffect(() => {
    dispatch(piloteActions.getPilotes());
  }, []);

  return (
    <div className="Pilotes">
      <h2 className="title-page">Pilotes</h2>
      {pilotes.map(pilote => {
        return (
          <>
            <p>{`${pilote.nomPilote} - ${pilote.ecuriePilote}`}</p>
          </>
        );
      })}
    </div>
  );
}
