import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { Button, Container, Row, Col } from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";

import piloteActions from "../../redux/actions/piloteActions";
import { getPilote } from "../../redux/reducers/piloteReducer";

export function Pilote() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { idPilote, anneeEnCours } = useParams();

  const pilote = useSelector(getPilote);

  const { nomPilote, ecuriePilote, photoPilote } = pilote;

  useEffect(() => {
    dispatch(piloteActions.getPiloteByIdAndYear({ anneeEnCours, idPilote }));
  }, [dispatch]);

  const onClickRetour = () => navigate("/pilotes");

  return (
    <div className="Pilotes">
      <h2 className="title-page">{nomPilote}</h2>
      <Container>
        <Row>
          <Col md={2}>
            <Button variant="outline-primary" onClick={onClickRetour}>
              Retour
            </Button>
          </Col>
        </Row>
        <Row className="mt-3">
          <Col md={{ span: 2, offset: 6 }}>
            {pilote.photoPilote ? (
              <img
                className="imgPilote"
                alt="image"
                src={`/images/pilotes/${pilote.photoPilote.replace(
                  "C:\\fakepath\\",
                  ""
                )}`}
              />
            ) : null}
          </Col>
        </Row>
      </Container>
    </div>
  );
}
