import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import errorHelper from "../../helpers/errorHelper";
import { formConstants } from "../../constants/formConstants";
import { FormInput } from "../Commons/FormInput/FormInput";
import ecurieActions from "../../redux/actions/ecurieActions";
import configHelper from "../../helpers/configHelper";
import { FormInputSelect } from "../Commons/FormSelect/FormInputSelect";

export function CreationEcurie() {
  let navigate = useNavigate();
  const dispatch = useDispatch();

  const [formErrors, setFormErrors] = useState({});
  const [formValues, setFormValues] = useState({
    nomEcurie: "",
    pilotesEcurie: [],
    anneeEnCours: "2021",
    nomPatron: "",
    moteur: "Mercedes",
    classement: 1
  });
  const [annees] = useState(configHelper.generateArrayOfYears());

  const {
    nomEcurie,
    pilotesEcurie,
    anneeEnCours,
    nomPatron,
    moteur,
    classement
  } = formValues;

  /**
   * Fonction appelée à la validation du formulaire
   * @param {*} values
   */
  const validateForm = values => {
    let errors = {};
    for (const [key, value] of Object.entries(values)) {
      if (key === "pilotesEcurie" && !value.includes("/")) {
        errors[key] = "Le format de ce champ n'est pas correct";
      }
      if (!value) {
        errors[key] = errorHelper.buildErrorFieldRequired(
          formConstants[key].libelleChamp
        );
      }
    }
    setFormErrors(errors);

    if (Object.keys(errors).length === 0) {
      return true;
    } else {
      return false;
    }
  };

  const navToEcuries = () => navigate("/ecuries");

  /**
   * Fonction appelée au clic sur le bouton de validation du formulaire
   */
  const send = () => {
    if (validateForm(formValues)) {
      if (formValues.pilotesEcurie.includes("/")) {
        const pilotes = formValues.pilotesEcurie.split("/");
        setFormValues({
          ...formValues,
          [event.target.name]: pilotes
        });
      }
      dispatch(
        ecurieActions.createEcurie(
          {
            nomEcurie,
            pilotesEcurie,
            anneeEnCours,
            nomPatron,
            moteur,
            classement
          },
          navToEcuries
        )
      );
    }
  };

  /**
   * Fonction appelée lors d'un changement sur un champ
   * @param {*} event
   */
  const handleChange = event => {
    setFormErrors({ ...formErrors, [event.target.name]: "" });

    setFormValues({
      ...formValues,
      [event.target.name]: event.target.value
    });
  };

  return (
    <div className="Login">
      <h2 className="title-page">Création d'écurie</h2>
      <Form>
        <FormInput
          handleChange={handleChange}
          formField={formConstants["nomEcurie"]}
          type="text"
          value={formValues["nomEcurie"]}
          formErrors={formErrors}
        />
        <FormInput
          handleChange={handleChange}
          formField={formConstants["pilotesEcurie"]}
          type="text"
          value={formValues["pilotesEcurie"]}
          formErrors={formErrors}
          placeholder="Format : Prénom Nom / Prénom nom"
        />
        <FormInput
          handleChange={handleChange}
          formField={formConstants["nomPatron"]}
          type="text"
          value={formValues["nomPatron"]}
          formErrors={formErrors}
        />
        <FormInputSelect
          handleChange={handleChange}
          formField={formConstants["anneeEnCours"]}
          options={annees}
          value={formValues["anneeEnCours"]}
          formErrors={formErrors}
        />
        <FormInputSelect
          handleChange={handleChange}
          formField={formConstants["moteur"]}
          options={configHelper.moteursList()}
          value={formValues["moteur"]}
          formErrors={formErrors}
        />
        <FormInputSelect
          handleChange={handleChange}
          formField={formConstants["classement"]}
          options={configHelper.generateArrayOfStandings()}
          value={formValues["classement"]}
          formErrors={formErrors}
        />
      </Form>
      <Button className="submit-button my-3" onClick={send} type="submit">
        Créer l'écurie
      </Button>
    </div>
  );
}
