import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Row,
  Col,
  Form,
  Button,
  Container,
  ButtonGroup,
  ButtonToolbar
} from "react-bootstrap";
import userActions from "../../redux/actions/userActions";
import { getUsers } from "../../redux/reducers/userReducer";

export function Dashboard() {
  const dispatch = useDispatch();
  const users = useSelector(getUsers);
  const [newUsers, setNewUsers] = useState([]);
  console.log("users", users);

  useEffect(() => {
    dispatch(userActions.getUsers()).then(res => setNewUsers(res));
  }, [dispatch]);
  console.log("newUsers", newUsers);

  const handleClick = id => {
    const user = newUsers.find(user => user._id === id);
    console.log("user", user);
    dispatch(userActions.updateDroitsUserById(user));
  };

  const handleSelect = (event, currentUser) => {
    console.log("event", event.target.checked);

    const index = newUsers.findIndex(user => user._id);
    newUsers[index] = {
      ...currentUser,
      [event.target.name]: event.target.checked
    };
  };

  return (
    <div className="Dashboard">
      <h1>Dashboard</h1>
      {newUsers.map((user, index) => (
        <Container key={index} fluid="md">
          <Row className="mb-2">
            <Col sm={2}>{user.email}</Col>
            <Col sm={4}>
              <Form.Check
                defaultChecked={user.droitsCreation}
                type="checkbox"
                name="droitsCreation"
                label="Droits de création"
                onChange={event => handleSelect(event, user)}
              />
            </Col>
            <Col sm={4}>
              <Form.Check
                defaultChecked={user.droitsEdition}
                type="checkbox"
                name="droitsEdition"
                label="Droits d'édition"
                onChange={event => handleSelect(event, user)}
              />
            </Col>
            <Col sm={2}>
              <ButtonToolbar>
                <ButtonGroup>
                  <Button
                    className="pr-1"
                    variant="success"
                    onClick={() => handleClick(user._id)}
                  >
                    <i className="bi-check2"></i>
                  </Button>
                </ButtonGroup>
                <ButtonGroup>
                  <Button
                    className="ml-1"
                    variant="danger"
                    onClick={() => handleClick(user._id)}
                  >
                    <i className="bi-trash"></i>
                  </Button>
                </ButtonGroup>
              </ButtonToolbar>
            </Col>
          </Row>
        </Container>
      ))}
    </div>
  );
}
