import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import errorHelper from "../../helpers/errorHelper";
import { formConstants } from "../../constants/formConstants";
import { FormInput } from "../Commons/FormInput/FormInput";
import piloteActions from "../../redux/actions/piloteActions";
import configHelper from "../../helpers/configHelper";
import { FormInputSelect } from "../Commons/FormSelect/FormInputSelect";

export function CreationPilote() {
  let navigate = useNavigate();
  const dispatch = useDispatch();

  const [formErrors, setFormErrors] = useState({});
  const [formValues, setFormValues] = useState({
    nomPilote: "",
    ecuriePilote: "",
    anneeEnCours: "2021",
    points: "",
    nationalite: "",
    photoPilote: ""
  });
  const [annees] = useState(configHelper.generateArrayOfYears());

  const {
    nomPilote,
    ecuriePilote,
    anneeEnCours,
    points,
    nationalite,
    photoPilote
  } = formValues;

  /**
   * Fonction appelée à la validation du formulaire
   * @param {*} values
   */
  const validateForm = values => {
    let errors = {};
    for (const [key, value] of Object.entries(values)) {
      if (!value) {
        errors[key] = errorHelper.buildErrorFieldRequired(
          formConstants[key].libelleChamp
        );
      }
    }
    setFormErrors(errors);

    if (Object.keys(errors).length === 0) {
      return true;
    } else {
      return false;
    }
  };

  const navToPilotes = () => navigate("/pilotes");

  /**
   * Fonction appelée au clic sur le bouton de validation du formulaire
   */
  const send = () => {
    if (validateForm(formValues)) {
      dispatch(
        piloteActions.createPilote(
          {
            nomPilote,
            ecuriePilote,
            anneeEnCours,
            points,
            nationalite,
            photoPilote
          },
          navToPilotes
        )
      );
    }
  };

  /**
   * Fonction appelée lors d'un changement sur un champ
   * @param {*} event
   */
  const handleChange = event => {
    console.log("event.target0", event.target.input);

    setFormErrors({ ...formErrors, [event.target.name]: "" });
    setFormValues({
      ...formValues,
      [event.target.name]: event.target.value
    });
  };

  return (
    <div className="Login">
      <h2 className="title-page">Création de pilote</h2>
      <Form>
        <FormInput
          handleChange={handleChange}
          formField={formConstants["nomPilote"]}
          type="text"
          value={formValues["nomPilote"]}
          formErrors={formErrors}
        />
        <FormInput
          handleChange={handleChange}
          formField={formConstants["ecuriePilote"]}
          type="text"
          value={formValues["ecuriePilote"]}
          formErrors={formErrors}
        />
        <FormInputSelect
          handleChange={handleChange}
          formField={formConstants["nationalite"]}
          options={configHelper.paysList()}
          value={formValues["nationalite"]}
          formErrors={formErrors}
        />
        <FormInputSelect
          handleChange={handleChange}
          formField={formConstants["anneeEnCours"]}
          options={annees}
          value={formValues["anneeEnCours"]}
          formErrors={formErrors}
        />
        <FormInput
          handleChange={handleChange}
          formField={formConstants["points"]}
          type="text"
          value={formValues["points"]}
          formErrors={formErrors}
        />
        <FormInput
          handleChange={handleChange}
          formField={formConstants["photoPilote"]}
          type="file"
          value={formValues["photoPilote"]}
          formErrors={formErrors}
        />
      </Form>
      <Button className="submit-button my-3" onClick={send} type="submit">
        Créer le pilote
      </Button>
    </div>
  );
}
