import piloteApi from "../../API/piloteApi";
import { piloteConstants } from "../../constants/piloteConstants";
import { commonConstants } from "../../constants/commonConstants";

const createPilote = (user, navigate) => dispatch => {
  return userApi
    .signup(user)
    .then(res => {
      dispatch({ type: piloteConstants.CREATE_PILOTE, payload: res.data });
      dispatch({
        type: commonConstants.NOTIFICATION_HANDLING,
        payload: res
      });

      navigate();
    })
    .catch(err => {
      console.log("test de l'erreur create pilote", err);

      return dispatch({
        type: commonConstants.NOTIFICATION_HANDLING,
        payload: err.response
      });
    });
};

const getPilotes = () => dispatch => {
  return piloteApi
    .getPilotes()
    .then(res => {
      dispatch({
        type: piloteConstants.GET_PILOTES,
        payload: res.data.pilotes
      });
    })
    .catch(err => {
      dispatch({
        type: commonConstants.NOTIFICATION_HANDLING,
        payload: err.response
      });

      console.log("test de l'erreur", err.response);
    });
};

const piloteActions = {
  createPilote,
  getPilotes
};
export default piloteActions;
