import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";

import ecurieActions from "../../redux/actions/ecurieActions";
import { getEcuries } from "../../redux/reducers/ecurieReducer";

export function Ecuries() {
  const dispatch = useDispatch();

  const ecuries = useSelector(getEcuries);

  useEffect(() => {
    dispatch(ecurieActions.getEcuries({ anneeEnCours: "2021" }));
  }, []);

  return (
    <div className="Ecuries">
      <h2 className="title-page">Ecuries</h2>
      {ecuries.map(ecurie => {
        return (
          <>
            <p>{`${ecurie.nomEcurie} - ${ecurie.ecurieEcurie}`}</p>
          </>
        );
      })}
    </div>
  );
}
