import axios from "axios";
const headers = {
  "Content-Type": "application/json"
};
const burl = "http://localhost:8800";

export default {
  createPilote: function(send) {
    return axios.post(`${burl}/pilote/create-pilote`, send, {
      headers: headers
    });
  },
  getPilotes: function(send) {
    return axios.post(`${burl}/pilote/get-pilotes`, send, {
      headers: headers
    });
  },
  deletePilote: function(send) {
    return axios.post(`${burl}/pilote/delete-pilote`, send, {
      headers: headers
    });
  },
  getPiloteByIdAndYear: function(send) {
    return axios.post(`${burl}/pilote/get-pilote`, send, {
      headers: headers
    });
  }
};
